import React, {Fragment, useState} from "react";



const Register = () => {

const [inputs, setInputs] = useState({
    email: "",
    password: "",
    name: ""
})

const {email,password,name} = inputs;

const onChange = (e) => {
    setInputs({...inputs, [e.target.name]
    : e.target.value});
};

    return (
        <Fragment>
            <h1 className="text-center my-5">Register</h1>
            <form>
                <input tupe="email" name="email" placeholder="email" className="form-control my-3" value={email} onChange = {e => onChange(e) }/>
                <input tupe="password" name="password" placeholder="password" className="form-control my-3" value={password} onChange = {e => onChange(e) }/>
                <input tupe="text" name="name" placeholder="name" className="form-control my-3" value={name} onChange = {e => onChange(e) }/>
                <button className="btn btn-success btn-block">Submit</button>
            </form>
            </Fragment>
    );
};


export default Register;
