import React, { Fragment, useState, useEffect } from "react";

const Create_User_Ticket = ({ setAuth }) => {
  
    const [id, setId] = useState();
    
    async function getId() {
        try {
          const response = await fetch("http://localhost:5000/dashboard/", {
            method: "POST",
            headers: { token: localStorage.token },
          });
    
          const parseRes = await response.json();
    
          setId(parseRes.user_id);
          console.log(parseRes.user_id);
        } catch (err) {
          console.error(err.message);
        }
      }

      useEffect(() => {
        getId();
      }, []);
/*
  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { id_owner, id_employee, id_issue, info, priority };
      const response = await fetch("http://localhost:5000/tickets", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      window.location = "/dashboard";
    } catch (err) {
      console.error(err.message);
    }
  };
  */
  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-info btn-lg"
        data-toggle="modal"
        data-target="#myModal"
      >
        Create New Ticket
      </button>

      <div id="myModal" class="modal fade" role="dialog">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Create New Ticket</h4>
            </div>
            <div class="modal-body">
            <h1> {id} </h1>








            </div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-success btn-lg"
                data-dismiss="modal"
              >
                Submit
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Create_User_Ticket;
