import React, { Fragment, useState, useEffect } from "react";
import { toast } from "react-toastify";

const Close_Admin_Ticket = ({ setAuth, ticket }) => {
  //edit description function

  //const [info, setInfo] = useState(ticket.ticket_information);
  //const [priority, setPriority] = useState(ticket.ticket_priority);
  const [status, setStatus] = useState(ticket.ticket_status);

  const closeTicket = async (e) => {
    e.preventDefault();
    try {
      const myHeaders = new Headers();

      myHeaders.append("Content-Type", "application/json");
      myHeaders.append("token", localStorage.token);
      const body = { status };
      console.log(status);
      const response = await fetch(
        `http://localhost:5000/dashboard_admin/tickets_closed/${ticket.ticket_id}`,
        {
          method: "PUT",
          headers: myHeaders,
          body: JSON.stringify(body),
        }
      );

      window.location = "/dashboard_admin";
      toast.success("Ticket Closed!");
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-danger btn-lg"
        value="CLOSED" onClick={(e) => setStatus(e.target.value)}
      >
        Close Ticket
      </button>

    </Fragment>
  );
};

export default Close_Admin_Ticket;
