import React, { Fragment, useState, useEffect } from "react";
import "./App.css";

import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";

//components

import Dashboard from "./components/Dashboard";
import Dashboard_Closed from "./components/Dashboard_Closed";
import Login from "./components/Login";
import Register from "./components/Register";
import Dashboard_Admin from "./components/Dashboard_Admin";
import Login_Admin from "./components/Login_Admin";



toast.configure();

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const setAuth = (boolean) => {
    setIsAuthenticated(boolean);
  };

  async function isAuth(){
    try {
      const response = await fetch("http://localhost:5000/auth/is-verify", {
        method: "GET",
        headers: { token: localStorage.token}
      });

      const parseRes = await response.json()

      parseRes === true ? setIsAuthenticated(true): setIsAuthenticated(false);
      
    } catch (err) {
      console.error(err.message);
      
    }
  }

  useEffect(() => {
    isAuth()
  })

  return (
    <Fragment>
      <Router>
        <div className="container-fluid">
          <Switch>
            <Route
              exact
              path="/login"
              render={(props) =>
                !isAuthenticated ? (
                  <Login {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/dashboard" />
                )
              }
            />
            <Route
              exact
              path="/register"
              render={(props) =>
                !isAuthenticated ? (
                  <Register {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
            <Route
              exact
              path="/dashboard"
              render={(props) =>
                isAuthenticated ? (
                  <Dashboard {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
            <Route
              exact
              path="/dashboard_closed"
              render={(props) =>
                isAuthenticated ? (
                  <Dashboard_Closed {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
            <Route
              exact
              path="/login_admin"
              render={(props) =>
                !isAuthenticated ? (
                  <Login_Admin {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/dashboard_admin" />
                )
              }
            />
            <Route
              exact
              path="/dashboard_admin"
              render={(props) =>
                isAuthenticated ? (
                  <Dashboard_Admin {...props} setAuth = {setAuth} />
                ) : (
                  <Redirect to="/login_admin" />
                )
              }
            />
          </Switch>
        </div>
      </Router>
    </Fragment> 
  );
}

export default App;
