import React, { Fragment, useState, useEffect } from "react";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";
//components

import List_User_Tickets from "./List_User_Tickets";
import List_User_Tickets_Closed from "./List_User_Tickets_Closed";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");
  const [id, setId] = useState();

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "POST",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.user_first_name);
    } catch (err) {
      console.error(err.message);
    }
  }
  async function getId() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "POST",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setId(parseRes.user_id);
      console.log(parseRes.user_id);
    } catch (err) {
      console.error(err.message);
    }
  };

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logged out successfully!");
  };

  useEffect(() => {
    getName();
    getId();
  }, []);

  return (
    <Fragment>
      <nav class="navbar navbar-dark sticky-top bg-dark flex-md-nowrap p-0">
        <a class="navbar-brand col-sm-3 col-md-2 mr-0">Diploma!</a>
        <ul class="navbar-nav px-3">
          <li class="nav-item text-nowrap">
            <button className="btn btn-primary" onClick={(e) => logout(e)}>
              Logout
            </button>
          </li>
        </ul>
      </nav>
      <h1>Welcome User {name}</h1>
      <List_User_Tickets></List_User_Tickets>
      <List_User_Tickets_Closed></List_User_Tickets_Closed>
      <button
        type="button"
        class="btn btn-info btn-lg"
        data-toggle="modal"
        data-target="#myModal"
      >
        Create New Ticket
      </button>

      <div id="myModal" class="modal fade" role="dialog">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">Create New Ticket</h4>
            </div>
            <div class="modal-body">
            <h1> {name} </h1>








            </div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-success btn-lg"
                data-dismiss="modal"
              >
                Submit
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

/*
<List_User_Tickets></List_User_Tickets>
        <List_User_Tickets_Closed></List_User_Tickets_Closed>
*/

export default Dashboard;
