import React, { Fragment, useState } from "react";

const Edit_User_Ticket = ({ ticket }) => {
    const [ticket_information, setDescription] = useState(ticket.ticket_information);

  //edit description function

  const updateDescription = async e => {
    e.preventDefault();
    try {
      const body = { ticket_information };
      const response = await fetch(
        `http://localhost:5000/dashboard/tickets/${ticket.ticket_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        }
      );

      window.location = "/";
    } catch (err) {
      console.error(err.message);
    }
  };
  return (
    <Fragment>
      <Fragment>
        <button
          type="button"
          class="btn btn-warning"
          data-toggle="modal"
          data-target={`#id${ticket.ticket_id}`}
        >
          Edit
        </button>

        {/* 
          id = id10
        */}
        <div
          class="modal"
          id={`id${ticket.ticket_id}`}
          onClick={() => setDescription(ticket.ticket_information)}
        >
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Edit Todo</h4>
                <button
                  type="button"
                  class="close"
                  data-dismiss="modal"
                  onClick={() => setDescription(ticket.ticket_information)}
                >
                  &times;
                </button>
              </div>

              <div class="modal-body">
                <input
                  type="text"
                  className="form-control"
                  value={ticket_information}
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>

              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-warning"
                  data-dismiss="modal"
                  onClick={(e) => updateDescription(e)}
                >
                  Edit
                </button>
                <button
                  type="button"
                  class="btn btn-danger"
                  data-dismiss="modal"
                  onClick={() => setDescription(ticket.ticket_information)}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    </Fragment>
  );
};

export default Edit_User_Ticket;
