import React, { Fragment, useState, useEffect } from "react";
import { toast } from "react-toastify";

const List_User_Tickets = ({ setAuth }) => {

    const [tickets, setTickets] = useState([]);

    const getTickets = async() => {
        try {

            const response = await fetch("http://localhost:5000/dashboard/tickets/")
            const jsonData = await response.json() 

            
            setTickets(jsonData);
        } catch (err) {
            console.log(err.message);
        }
    };

    useEffect(() => {
        getTickets();
    });

    console.log(tickets);
    return(
        <Fragment>
            <h1> List of User Tickets </h1>
            {" "}
            <table className="table mt-5 text-center">
                <thead>
                    <tr>
                        <th> ID </th>
                        <th> ID OWNER </th>
                        <th> ID ASSIGNED </th>
                        <th> ID ISSUE </th>
                        <th> INFORMATION </th>
                        <th> DATE OPEN </th>
                        <th> DATE CLOSED </th>
                        <th> STATUS </th>
                        <th> PRIORITY </th>
                        <th> STATUS </th>
                    </tr>
                </thead>
                <tbody>
                    {/*<tr>
                        <td> ID </td>
                        <td> ID </td>
                        <td> ID </td>
                        <td> ID </td>
                        <td> Info </td>
                        <td> Date_open </td> 
                        <td> Date_closed </td>
                        <td> Status </td>
                        <td> Priority </td>
                        <td> Status </td> 
                    </tr>  */}
                    <tr>
                        <td> ID </td>
                        <td> ID </td>
                        <td> ID </td>
                        <td> ID </td>
                        <td> Info </td>
                        <td> Date_open </td> 
                        <td> Date_closed </td>
                        <td> Status </td>
                        <td> Priority </td>
                        <td> Status </td> 
                    </tr>
                </tbody>
            </table>
        </Fragment>
    );
};

export default List_User_Tickets;
